import React, { useState } from 'react';
import './index.css';
import { Container, Row, Col, Label, Input } from 'reactstrap';

import { dataList } from '../data';

type Props = {
  selectValue: string;
  setSelectValue: (selected: string) => void;
}

function SelectList({ selectValue, setSelectValue }: Props) {

  return (
    <>
      <Label for="exampleSelect">[Select Field]</Label>
      <Input type="select" name="select" defaultValue="Please select item to add list" value={selectValue} id="exampleSelect" bsSize="lg" onChange={(event) => setSelectValue(event.target.value)} >
        {MakeOption()}
      </Input>
    </>
  );
}

function MakeOption() {
  const list = dataList({ count: 10, text: 'option list' });
  return list.map((text) => <option value={text}> {text} </option>);
}

export default SelectList;
